# Process each row
for idx, row in df.iterrows():
    species = str(row['SPECIES']).lower().strip()
    
    if species in plant_database:
        data = plant_database[species]
        
        # Update fields only if they're empty
        for field, value in data.items():
            df_field = field.upper().replace('_', ' ')
            if df_field in df.columns:
                if pd.isna(row[df_field]) or row[df_field] == '':
                    df.at[idx, df_field] = value

# Save the final result
df.to_csv('data/enhanced_species_table_final.csv', index=False)
print(f"Processed {len(df)} rows - final comprehensive update complete")